(208, 37, '_form', '<div class=\"row\">\n\n <div class=\"col-md-6 col-lg-6\">\n   <label>Name</label>\n   [text* pd-name]\n </div>\n\n <div class=\"col-md-6 col-lg-6\">\n   <label>Email</label>\n   [email* pd-email]\n </div>\n\n <div class=\"col-md-6 col-lg-6\">\n   <label>Phone</label>\n   [tel* pd-phone]\n </div>\n\n\n <div class=\"col-md-6 col-lg-6\">\n   <label>City</label>\n   [select city \"Colombo\" \"Galle\" \"Ja-Ela\" \"Matara\" \"Jafna\" \"Kalutara\"]\n </div>\n\n <div class=\"col-lg-12\">\n   <label>Information About</label>\n   [select information-about \"Job Market In GTA\" \"Lorem Ipsum\" \"Doler Sit Amet\"]\n </div>\n\n <div class=\"col-lg-12 pd-checkbox-div\">\n  <label>[checkbox checkbox-677 use_label_element \"\"]   Check this box to allow Employment and Education Network Canada to communicate with you. I read & understood the <a href=\"#\" target=\"_blank\">Terms </a>Provided.</label>   \n </div>\n\n<div class=\"col-lg-12 pd-red-btn-sec text-right\">\n[submit \"Send Request\"]\n</div>\n\n</div>'),
